public String post(PageModel model, @MethodParam("getAccount") @BindParams Account account, @BindParams User user,
	                   @BindParams OtherAccountData otherAccountData, @SpringBean("userService") UserService userService,
	                   @SpringBean("messageSourceService") MessageSourceService messageSourceService,
	                   @SpringBean("adminAccountService") AccountService accountService,
	                   @SpringBean("adminService") AdministrationService administrationService,
	                   @SpringBean("adminUiAccountValidator") AdminUiAccountValidator accountValidator,
	                   @SpringBean("providerManagementService") ProviderManagementService providerManagementService,
					   @SpringBean("appFrameworkService") AppFrameworkService appFrameworkService,
	                   HttpServletRequest request, UiUtils uu) throws IOException {

		Errors errors = new BeanPropertyBindingResult(account, "account");

        List<Extension> customUserPropertyEditFragments =
				appFrameworkService.getExtensionsForCurrentUser("userAccount.userPropertyEditFragment");
        Map<String, String[]> parameterMap = request.getParameterMap();
        for(Extension ext : customUserPropertyEditFragments) {
            if (StringUtils.equals(ext.getExtensionParams().get("type").toString(), "userProperty")) {
                String userPropertyName = ext.getExtensionParams().get("userPropertyName").toString();
                String[] parameterValues = parameterMap.get(userPropertyName);
                if (parameterValues != null && parameterValues.length > 0) {
                    String parameterValue;
                    if (userPropertyName == "locationUuid") {
                    	parameterValue = String.join(",", parameterValues);
                    } else {
                        if (parameterValues.length > 1) {
                            log.warn("Multiple userProperty for a single user type not supported, ignoring extra values");
                        }
                        parameterValue = parameterValues[0];
                    }
                    if (userPropertyName != null && parameterValue != null) {
                        user.setUserProperty(userPropertyName, parameterValue);
                    }
                }
            }
        }

        List<Extension> customPersonAttributeEditFragments =
				appFrameworkService.getExtensionsForCurrentUser("userAccount.personAttributeEditFragment");
        for(Extension ext : customPersonAttributeEditFragments) {
            if (StringUtils.equals(ext.getExtensionParams().get("type").toString(), "personAttribute")) {
                String formFiledName = ext.getExtensionParams().get("formFieldName").toString();
                String personAttributeTypeUuid = ext.getExtensionParams().get("uuid").toString();
                String[] parameterValues = parameterMap.get(formFiledName);
                if (parameterValues != null && parameterValues.length > 0) {
                    if (parameterValues.length > 1) {
                        log.warn("Multiple values for a single person attribute type not supported, ignoring extra values");
                    }
                    String parameterValue = parameterValues[0];
                    if (parameterValue != null) {
                        PersonAttributeType personAttributeByUuid = Context.getPersonService()
                                .getPersonAttributeTypeByUuid(personAttributeTypeUuid);
                        if (personAttributeByUuid != null) {
                            PersonAttribute attribute = new PersonAttribute(personAttributeByUuid, parameterValue);
                            account.getPerson().addAttribute(attribute);
                        }
                    }
                }
            }
        }

		if (otherAccountData.getAddUserAccount()) {
			//The StringToRoleConverter emrapi for some reason is taking precedence over the
			//one in uiframework module and it doesn't get role by uuid, so we have to do this
			user.addRole(userService.getRoleByUuid(request.getParameter("privilegeLevel")));
			String[] uuids = request.getParameterValues("capabilities");
			if (uuids != null) {
				for (String uuid : uuids) {
					user.addRole(userService.getRoleByUuid(uuid));
				}
			}

			String forcePassword = otherAccountData.getForceChangePassword() ? "true" : "false";
			user.setUserProperty(OpenmrsConstants.USER_PROPERTY_CHANGE_PASSWORD, forcePassword);
			account.addUserAccount(user);
		}
		if (otherAccountData.getAddProviderAccount()) {
			Provider provider = new Provider();
			provider.setIdentifier(request.getParameter("identifier"));
			provider.setProviderRole(providerManagementService.getProviderRoleByUuid(request.getParameter("providerRole")));
			account.addProviderAccount(provider);
		}

		accountValidator.validate(account, errors);

		if (!errors.hasErrors()) {
			try {
				account.setPassword(user, otherAccountData.getPassword());
				accountService.saveAccount(account);
				InfoErrorMessageUtil.flashInfoMessage(request.getSession(), "adminui.account.saved");
				return "redirect:/adminui/systemadmin/accounts/manageAccounts.page";
			}
			catch (Exception e) {
				errors.reject("adminui.account.error.save.fail");
				//If the person, provider or user account had been flushed we need to unset the ids because
				//they actually don't exist in the DB otherwise the logic in the GSP will see the ids and
				//think we are editing and things will break
				account.getPerson().setId(null);
				if (otherAccountData.getAddProviderAccount()) {
					account.getProviderAccounts().get(0).setProviderId(null);
				}
				if (otherAccountData.getAddUserAccount()) {
					account.getUserAccounts().get(0).setUserId(null);
				}
			}
		}

		setModelAttributes(model, account, otherAccountData, accountService, administrationService,
		    providerManagementService, uu, appFrameworkService);

		sendErrorMessage(errors, model, messageSourceService, request);

		if (account.getPerson().getPersonId() == null) {
			setJsonFormData(model, account, otherAccountData);
		}

		return "systemadmin/accounts/account";

	}

private void setJsonFormData(PageModel model, Account account, OtherAccountData otherAccountData) throws IOException {

		ObjectMapper mapper = new ObjectMapper();
		SimpleObject simplePerson = new SimpleObject();
		simplePerson.put("familyName", account.getFamilyName());
		simplePerson.put("givenName", account.getGivenName());
		simplePerson.put("gender", account.getGender() != null ? account.getGender() : "");
		model.addAttribute("personJson", mapper.writeValueAsString(simplePerson));

		SimpleObject simpleUser = new SimpleObject();
		SimpleObject simpleProvider = new SimpleObject();
		if (otherAccountData != null) {
			if (otherAccountData.getAddUserAccount()) {
				User u = account.getUserAccounts().get(0);
				simpleUser.put("username", u.getUsername());
				simpleUser.put("privilegeLevel", account.getPrivilegeLevel(u).getUuid());
				SimpleObject userProperties = new SimpleObject();
				userProperties
				        .put(OpenmrsConstants.USER_PROPERTY_CHANGE_PASSWORD, otherAccountData.getForceChangePassword());
				simpleUser.put("userProperties", userProperties);
				SimpleObject simpleUserCapabilities = new SimpleObject();
				for (Role cap : account.getCapabilities(u)) {
					simpleUserCapabilities.put(cap.getUuid(), true);
				}
				simpleUser.put("capabilities", simpleUserCapabilities);
			}

			if (otherAccountData.getAddProviderAccount()) {
				Provider prov = (Provider) account.getProviderAccounts().get(0);
				simpleProvider.put("identifier", prov.getIdentifier());
				simpleProvider.put("providerRole", prov.getProviderRole().getUuid());
			}
		}

		model.addAttribute("userJson", mapper.writeValueAsString(simpleUser));
		model.addAttribute("providerJson", mapper.writeValueAsString(simpleProvider));
	}

private void submitForm() {
        ApplicationContext ac = AppUtil.getApplicationContext();
        WorkflowUserManager workflowUserManager = (WorkflowUserManager) ac.getBean("workflowUserManager");
        UserDao userDao = (UserDao) ac.getBean("userDao");
        User userObject = userDao.getUser(workflowUserManager.getCurrentUsername());
        User currentUser = null;
        if (userObject != null) {
            currentUser = new User();
            BeanUtils.copyProperties(userObject, currentUser);
        }
        ExtDirectoryManager directoryManager = (ExtDirectoryManager) AppUtil.getApplicationContext().getBean("directoryManager");

        Collection<String> errors = new ArrayList<String>();
        Collection<String> passwordErrors = new ArrayList<String>();

        boolean authenticated = false;
        if (currentUser != null) {
            if (!currentUser.getUsername().equals(getRequestParameterString("username"))) {
                HttpServletRequest request = WorkflowUtil.getHttpServletRequest();
                if (request != null) {
                    HttpSession session = request.getSession(false);
                    if (session != null) {
                        session.invalidate();
                        authenticated = false;
                    }
                }
            } else {
                try {
                    if (directoryManager.authenticate(currentUser.getUsername(), getRequestParameterString("oldPassword"))) {
                        authenticated = true;
                    }
                } catch (Exception e) { }
            }
        }
        UserSecurity us = DirectoryUtil.getUserSecurity();

        if ("".equals(getPropertyString("f_firstName"))) {
            currentUser.setFirstName(getRequestParameterString("firstName"));
        }

        if ("".equals(getPropertyString("f_lastName"))) {
            currentUser.setLastName(getRequestParameterString("lastName"));
        }

        if ("".equals(getPropertyString("f_email"))) {
            currentUser.setEmail(getRequestParameterString("email"));
        }

        if ("".equals(getPropertyString("f_timeZone"))) {
            currentUser.setTimeZone(getRequestParameterString("timeZone"));
        }

        if ("".equals(getPropertyString("f_locale"))) {
            currentUser.setLocale(getRequestParameterString("locale"));
        }

        if (!authenticated) {
            if (errors == null) {
                errors = new ArrayList<String>();
            }
            errors.add(ResourceBundleUtil.getMessage("console.directory.user.error.label.authenticationFailed"));
        } else {
            if (us != null) {
                errors = us.validateUserOnProfileUpdate(currentUser);
            }

            if (getRequestParameterString("password") != null && !getRequestParameterString("password").isEmpty() && us != null) {
                passwordErrors = us.validatePassword(getRequestParameterString("username"),  getRequestParameterString("oldPassword"), getRequestParameterString("password"), getRequestParameterString("confirmPassword")); 
            }
        }

        setProperty("errors", errors);
        if (passwordErrors != null && !passwordErrors.isEmpty()) {
            setProperty("passwordErrors", passwordErrors);
        }

        if (authenticated && (passwordErrors != null && passwordErrors.isEmpty()) && (errors != null && errors.isEmpty())) {
            if ("".equals(getPropertyString("f_password"))) {
                if (getRequestParameterString("password") != null && getRequestParameterString("confirmPassword") != null && getRequestParameterString("password").length() > 0 && getRequestParameterString("password").equals(getRequestParameterString("confirmPassword"))) {
                    if (us != null) {
                        currentUser.setPassword(us.encryptPassword(getRequestParameterString("username"), getRequestParameterString("password")));
                    } else {
                        currentUser.setPassword(StringUtil.md5Base16(getRequestParameterString("password")));
                    }
                    currentUser.setConfirmPassword(getRequestParameterString("password"));
                }
            }

            if (currentUser.getUsername().equals(getRequestParameterString("username"))) {
                userDao.updateUser(currentUser);
                if (us != null) {
                    us.updateUserProfilePostProcessing(currentUser);
                }

                setAlertMessage(getPropertyString("message"));
                setProperty("headerTitle", getPropertyString("label"));
                if (getPropertyString("redirectURL") != null && !getPropertyString("redirectURL").isEmpty()) {
                    setProperty("view", "redirect");
                    boolean redirectToParent = "Yes".equals(getPropertyString("showInPopupDialog"));
                    setRedirectUrl(getPropertyString("redirectURL"), redirectToParent);
                } else {
                    setProperty("saved", "true");
                    viewForm(null);
                }
            }
        } else {
            viewForm(currentUser);
        }
    }

private static List<Pattern> getAllowedRegexp3(Map<String, AntiSamyPattern> commonRegularExpressions1, Element ele, String name) throws PolicyException {
        List<Pattern> allowedRegExp = new ArrayList<Pattern>();
        for (Element regExpNode : getGrandChildrenByTagName(ele, "regexp-list", "regexp")) {
            String regExpName = getAttributeValue(regExpNode, "name");
            String value = getAttributeValue(regExpNode, "value");

            AntiSamyPattern pattern = commonRegularExpressions1.get(regExpName);

            if (pattern != null) {
                allowedRegExp.add(pattern.getPattern());
            } else if (value != null) {
                allowedRegExp.add(Pattern.compile(REGEXP_BEGIN + value + REGEXP_END));
            } else {
                throw new PolicyException("Regular expression '" + regExpName + "' was referenced as a common regexp in definition of '" + name + "', but does not exist in <common-regexp>");
            }
        }
        return allowedRegExp;
    }

private static List<Pattern> getAllowedRegexps(Map<String, AntiSamyPattern> commonRegularExpressions1, Element ele) {
        List<Pattern> allowedRegExp = new ArrayList<Pattern>();
        for (Element regExpNode : getGrandChildrenByTagName(ele, "regexp-list", "regexp")) {
            String regExpName = getAttributeValue(regExpNode, "name");
            String value = getAttributeValue(regExpNode, "value");

            if (regExpName != null && regExpName.length() > 0) {
                allowedRegExp.add(commonRegularExpressions1.get(regExpName).getPattern());
            } else {
                allowedRegExp.add(Pattern.compile(REGEXP_BEGIN + value + REGEXP_END));
            }
        }
        return allowedRegExp;
    }

private static List<Pattern> getAllowedRegexps2(Map<String, AntiSamyPattern> commonRegularExpressions1, Element attributeNode, String tagName) throws PolicyException {
        List<Pattern> allowedRegexps = new ArrayList<Pattern>();
        for (Element regExpNode : getGrandChildrenByTagName(attributeNode, "regexp-list", "regexp")) {
            String regExpName = getAttributeValue(regExpNode, "name");
            String value = getAttributeValue(regExpNode, "value");

            /*
            * Look up common regular expression specified
            * by the "name" field. They can put a common
            * name in the "name" field or provide a custom
            * value in the "value" field. They must choose
            * one or the other, not both.
            */
            if (regExpName != null && regExpName.length() > 0) {

                AntiSamyPattern pattern = commonRegularExpressions1.get(regExpName);
                if (pattern != null) {
                    allowedRegexps.add(pattern.getPattern());
                } else {
                    throw new PolicyException("Regular expression '" + regExpName + "' was referenced as a common regexp in definition of '" + tagName + "', but does not exist in <common-regexp>");
                }

            } else if (value != null && value.length() > 0) {
                allowedRegexps.add(Pattern.compile(REGEXP_BEGIN + value + REGEXP_END));
            }
        }
        return allowedRegexps;
    }

