private static boolean unzipNonStrict(InputStream in, VFSContainer targetDir, Identity identity, boolean versioning) {
		try(net.sf.jazzlib.ZipInputStream oZip = new net.sf.jazzlib.ZipInputStream(in)) {
			VFSRepositoryService vfsRepositoryService = CoreSpringFactory.getImpl(VFSRepositoryService.class);

			// unzip files
			net.sf.jazzlib.ZipEntry oEntr = oZip.getNextEntry();

			VFSLeaf lastLeaf = null;
			while (oEntr != null) {
				if (oEntr.getName() != null && !oEntr.getName().startsWith(DIR_NAME__MACOSX)) {
					if (oEntr.isDirectory()) {
						// skip MacOSX specific metadata directory
						// create directories
						getAllSubdirs(targetDir, oEntr.getName(), identity, true);
					} else {
						// create file
						VFSContainer createIn = targetDir;
						String name = oEntr.getName();
						// check if entry has directories which did not show up as
						// directories above
						int dirSepIndex = name.lastIndexOf('/');
						if (dirSepIndex == -1) {
							// try it windows style, backslash is also valid format
							dirSepIndex = name.lastIndexOf('\\');
						}
						if (dirSepIndex > 0) {
							// create subdirs
							createIn = getAllSubdirs(targetDir, name.substring(0, dirSepIndex), identity, true);
							if (createIn == null) {
								log.debug("Error creating directory structure for zip entry: {}", oEntr.getName());
								return false;
							}
							name = name.substring(dirSepIndex + 1);
						}

						if(name != null && name.startsWith("._oo_meta_")) {
							if(lastLeaf != null && name.endsWith(lastLeaf.getName())) {
								unzipMetadata(oZip, lastLeaf);
							}
						} else if(versioning) {
							VFSLeaf newEntry = (VFSLeaf)createIn.resolve(name);
							if(newEntry == null) {
								newEntry = createIn.createChildLeaf(name);
								if (!copyShielded(oZip, newEntry)) {
									return false;
								}
							} else if (newEntry.canVersion() == VFSConstants.YES) {
								vfsRepositoryService.addVersion(newEntry, identity, "", oZip);
							}
							lastLeaf = newEntry;
							unzipMetadata(identity, newEntry);
						} else {
							VFSLeaf newEntry = createIn.createChildLeaf(name);
							if (newEntry != null) {
								if (!copyShielded(oZip, newEntry)) {
									return false;
								}
								lastLeaf = newEntry;
								unzipMetadata(identity, newEntry);
							}
						}
					}
				}
				oZip.closeEntry();
				oEntr = oZip.getNextEntry();
			}
		} catch (IOException e) {
			return false;
		}
		return true;
	}

public static List<String> checkLockedFileBeforeUnzipNonStrict(VFSLeaf zipLeaf, VFSContainer targetDir, Identity identity) {
		List<String> lockedFiles = new ArrayList<>();
		VFSLockManager vfsLockManager = CoreSpringFactory.getImpl(VFSLockManager.class);

		try(InputStream in = zipLeaf.getInputStream();
				net.sf.jazzlib.ZipInputStream oZip = new net.sf.jazzlib.ZipInputStream(in);) {
			// unzip files
			net.sf.jazzlib.ZipEntry oEntr = oZip.getNextEntry();
			while (oEntr != null) {
				if (oEntr.getName() != null && !oEntr.getName().startsWith(DIR_NAME__MACOSX)) {
					if (oEntr.isDirectory()) {
						// skip MacOSX specific metadata directory
						// directories aren't locked
						oZip.closeEntry();
						oEntr = oZip.getNextEntry();
						continue;
					} else {
						// search file
						VFSContainer createIn = targetDir;
						String name = oEntr.getName();
						// check if entry has directories which did not show up as
						// directories above
						int dirSepIndex = name.lastIndexOf('/');
						if (dirSepIndex == -1) {
							// try it windows style, backslash is also valid format
							dirSepIndex = name.lastIndexOf('\\');
						}
						if (dirSepIndex > 0) {
							// get subdirs
							createIn = getAllSubdirs(targetDir, name.substring(0, dirSepIndex), identity, false);
							if (createIn == null) {
								//sub directories don't exist, and aren't locked
								oZip.closeEntry();
								oEntr = oZip.getNextEntry();
								continue;
							}
							name = name.substring(dirSepIndex + 1);
						}

						VFSLeaf newEntry = (VFSLeaf)createIn.resolve(name);
						if(vfsLockManager.isLockedForMe(newEntry, identity, VFSLockApplicationType.vfs, null)) {
							lockedFiles.add(name);
						}
					}
				}
				oZip.closeEntry();
				oEntr = oZip.getNextEntry();
			}
		} catch (IOException e) {
			return null;
		}

		return lockedFiles;
	}

private void processReturnFiles(VFSLeaf target, List<BulkAssessmentRow> rows) {
		Map<String, BulkAssessmentRow> assessedIdToRow = new HashMap<>();
		for(BulkAssessmentRow row:rows) {
			assessedIdToRow.put(row.getAssessedId(), row);
		}

		if(target.exists()) {
			File parentTarget = ((LocalImpl)target).getBasefile().getParentFile();

			ZipEntry entry;
			try(InputStream is = target.getInputStream();
					ZipInputStream zis = new ZipInputStream(is)) {
				byte[] b = new byte[FileUtils.BSIZE];
				while ((entry = zis.getNextEntry()) != null) {
					if(!entry.isDirectory()) {
						while (zis.read(b) > 0) {
							//continue
						}

						Path op = new File(parentTarget, entry.getName()).toPath();
						if(!Files.isHidden(op) && !op.toFile().isDirectory()) {
							Path parentDir = op.getParent();
							String assessedId = parentDir.getFileName().toString();
							String filename = op.getFileName().toString();

							BulkAssessmentRow row;
							if(assessedIdToRow.containsKey(assessedId)) {
								row = assessedIdToRow.get(assessedId);
							} else {
								row = new BulkAssessmentRow();
								row.setAssessedId(assessedId);
								assessedIdToRow.put(assessedId, row);
								rows.add(row);
							}

							if(row.getReturnFiles() == null) {
								row.setReturnFiles(new ArrayList<String>(2));
							}
							row.getReturnFiles().add(filename);
						}
					}
				}
			} catch(Exception e) {
				logError("", e);
			}
		}
	}

@Override
	public File getResource(String path) {
		if (resourceShortcuts != null && resourceShortcuts.containsKey(path))
			path = resourceShortcuts.get(path);

		if (developmentFolders != null) {
			for (File developmentFolder : developmentFolders) {
	    		// we're in development mode, and we want to dynamically reload resource from this filesystem directory
				File file = new File(developmentFolder, path);
				if (file.exists()) {
					return file;
				}
			}
			return null;
    	}
    	else {
    		ModuleClassLoader mcl = moduleClassLoader != null ? moduleClassLoader : (ModuleClassLoader) getClass().getClassLoader();

    		// force OpenMRS to expand this resource from the jar, if available.
    		// ideally we'd only look in this module, but this will also look in required modules...
    		mcl.findResource(resourcePrefix + path);

    		File folderForModule = ModuleClassLoader.getLibCacheFolderForModule(mcl.getModule());
    		File resourceFile = new File(folderForModule, resourcePrefix + path);
    		return resourceFile.exists() ? resourceFile : null;
    	}
	}

@Override
	public void contextRefreshed() {
		contextLastRefreshedTime = System.currentTimeMillis();

		// START HACK
		// since we're not using a Listener anymore, these are not set at startup
		try {
			Class<?> webConstants1x = Context.loadClass("org.openmrs.web.WebConstants");
			String webappName = (String) webConstants1x.getField("WEBAPP_NAME").get(null);
			WebConstants.CONTEXT_PATH = webappName;
			WebConstants.WEBAPP_NAME = webappName;
		} catch (Exception ex) {
			log.error("Failed to get CONTEXT_PATH from WebConstants during UI Framework startup");
		}
		// END HACK

		PageFactory pageFactory = getComponent(PageFactory.class);
		FragmentFactory fragmentFactory = getComponent(FragmentFactory.class);
		ResourceFactory resourceFactory = getComponent(ResourceFactory.class);

		// Register a standard resource provider that can load file-based resources
		resourceFactory.addResourceProvider(FileResourceProvider.RESOURCE_KEY, new FileResourceProvider());

		List<UiContextRefreshedCallback> callbacks = Context.getRegisteredComponents(UiContextRefreshedCallback.class);
		for (UiContextRefreshedCallback callback : callbacks) {
			try {
				callback.afterContextRefreshed(pageFactory, fragmentFactory, resourceFactory);
			}
			catch (Exception ex) {
				log.error("Error in UiContextRefreshedCallback: " + callback, ex);
			}
		}
	}

