@Override
	public void validate(Object obj, Errors errors) {
		HtmlForm hf = (HtmlForm) obj;
		// can't use ValidationUtil for this because toString of a new non-null form is ""
		if (hf.getForm() == null)
			errors.rejectValue("form", "error.null");
		ValidationUtils.rejectIfEmptyOrWhitespace(errors, "name", "error.null");
		ValidationUtils.rejectIfEmptyOrWhitespace(errors, "xmlData", "error.null");

		if (hf.getForm() != null) {
			errors.pushNestedPath("form");
			new FormValidator().validate(hf.getForm(), errors);
			errors.popNestedPath();
		}
		if (hf.getXmlData() != null) {
			try {
				@SuppressWarnings("unused")
				FormEntrySession session = new FormEntrySession(HtmlFormEntryUtil.getFakePerson(), hf.getXmlData(), null); // can't access an HttpSession here
				if (hf.getForm() != null) {
					if (hf.getForm().getEncounterType() != null && hasEncounterTypeTag(hf.getXmlData())) {
						throw new FormEntryException(
						        "encounterType tag is not allowed for a form that is already associated to encounter type");
					}
				}
				HtmlFormEntryGenerator htmlGenerator = new HtmlFormEntryGenerator();
				String xml = hf.getXmlData();
				xml = htmlGenerator.substituteCharacterCodesWithAsciiCodes(xml);
				xml = htmlGenerator.stripComments(xml);
				xml = htmlGenerator.convertSpecialCharactersWithinLogicAndVelocityTests(xml);
				xml = htmlGenerator.applyRoleRestrictions(xml);
				xml = htmlGenerator.applyMacros(session, xml);
				xml = htmlGenerator.applyRepeats(xml);
				Document document = HtmlFormEntryUtil.stringToDocument(xml);
				validateTags(document, errors, null);
			}
			catch (Exception ex) {
				errors.rejectValue("xmlData", null, ex.getMessage());
				log.warn("Error in HTML form", ex);
			}
		}
	}

@RequestMapping("/module/htmlformentry/htmlFormFromFile.form")
	public void handleRequest(Model model, @RequestParam(value = "filePath", required = false) String filePath,
	        @RequestParam(value = "patientId", required = false) Integer pId,
	        @RequestParam(value = "isFileUpload", required = false) boolean isFileUpload, HttpServletRequest request)
	        throws Exception {

		Context.requirePrivilege("Manage Forms");

		if (log.isDebugEnabled())
			log.debug("In reference data...");

		model.addAttribute("previewHtml", "");
		String message = "";
		File f = null;
		try {
			if (isFileUpload) {
				MultipartHttpServletRequest multipartRequest = (MultipartHttpServletRequest) request;
				MultipartFile multipartFile = multipartRequest.getFile("htmlFormFile");
				if (multipartFile != null) {
					//use the same file for the logged in user
					f = new File(SystemUtils.JAVA_IO_TMPDIR,
					        TEMP_HTML_FORM_FILE_PREFIX + Context.getAuthenticatedUser().getSystemId());
					if (!f.exists())
						f.createNewFile();

					filePath = f.getAbsolutePath();
					FileOutputStream fileOut = new FileOutputStream(f);
					IOUtils.copy(multipartFile.getInputStream(), fileOut);
					fileOut.close();
				}
			} else {
				if (StringUtils.hasText(filePath)) {
					f = new File(filePath);
				} else {
					message = "You must specify a file path to preview from file";
				}
			}

			if (f != null && f.exists() && f.canRead()) {
				model.addAttribute("filePath", filePath);

				StringWriter writer = new StringWriter();
				IOUtils.copy(new FileInputStream(f), writer, "UTF-8");
				String xml = writer.toString();

				Patient p = null;
				if (pId != null) {
					p = Context.getPatientService().getPatient(pId);
				} else {
					p = HtmlFormEntryUtil.getFakePerson();
				}
				HtmlForm fakeForm = new HtmlForm();
				fakeForm.setXmlData(xml);
				FormEntrySession fes = new FormEntrySession(p, null, Mode.ENTER, fakeForm, request.getSession());
				String html = fes.getHtmlToDisplay();
				if (fes.getFieldAccessorJavascript() != null) {
					html += "<script>" + fes.getFieldAccessorJavascript() + "</script>";
				}
				model.addAttribute("previewHtml", html);
				//clear the error message
				message = "";
			} else {
				message = "Please specify a valid file path or select a valid file.";
			}
		}
		catch (Exception e) {
			log.error("An error occurred while loading the html.", e);
			message = "An error occurred while loading the html. " + e.getMessage();
		}

		model.addAttribute("message", message);
		model.addAttribute("isFileUpload", isFileUpload);
	}

public static String pathOffset(String path, RoutingContext context) {
    final Route route = context.currentRoute();

    // cannot make any assumptions
    if (route == null) {
      return path;
    }

    if (!route.isExactPath()) {
      final String rest = context.pathParam("*");
      if (rest != null) {
        // normalize
        if (rest.length() > 0) {
          if (rest.charAt(0) == '/') {
            return rest;
          } else {
            return "/" + rest;
          }
        } else {
          return "/";
        }
      }
    }
    int prefixLen = 0;
    String mountPoint = context.mountPoint();
    if (mountPoint != null) {
      prefixLen = mountPoint.length();
      // special case we need to verify if a trailing slash  is present and exclude
      if (mountPoint.charAt(mountPoint.length() - 1) == '/') {
        prefixLen--;
      }
    }
    // we can only safely skip the route path if there are no variables or regex
    if (!route.isRegexPath()) {
      String routePath = route.getPath();
      if (routePath != null) {
        prefixLen += routePath.length();
        // special case we need to verify if a trailing slash  is present and exclude
        if (routePath.charAt(routePath.length() - 1) == '/') {
          prefixLen--;
        }
      }
    }
    return prefixLen != 0 ? path.substring(prefixLen) : path;
  }

protected Response attachToPost(Message mess, String filename, InputStream file,  HttpServletRequest request) {
		Identity identity = getIdentity(request);
		if(identity == null) {
			return Response.serverError().status(Status.UNAUTHORIZED).build();
		} else if (!identity.equalsByPersistableKey(mess.getCreator())) {
			if(mess.getModifier() == null || !identity.equalsByPersistableKey(mess.getModifier())) {
				return Response.serverError().status(Status.UNAUTHORIZED).build();
			}
		}

		VFSContainer container = fom.getMessageContainer(mess.getForum().getKey(), mess.getKey());
		VFSItem item = container.resolve(filename);
		VFSLeaf attachment = null;
		if(item == null) {
			attachment = container.createChildLeaf(filename);
		} else {
			filename = VFSManager.rename(container, filename);
			if(filename == null) {
				return Response.serverError().status(Status.NOT_ACCEPTABLE).build();
			}
			attachment = container.createChildLeaf(filename);
		}


		try(OutputStream out = attachment.getOutputStream(false)) {
			IOUtils.copy(file, out);
		} catch (IOException e) {
			return Response.serverError().status(Status.INTERNAL_SERVER_ERROR).build();
		} finally {
			FileUtils.closeSafely(file);
		}
		return Response.ok().build();
	}

protected Response attachToPost(Long messageKey, String filename, InputStream file,  HttpServletRequest request) {
		//load message
		Message mess = fom.loadMessage(messageKey);
		if(mess == null) {
			return Response.serverError().status(Status.NOT_FOUND).build();
		}
		if(!forum.equalsByPersistableKey(mess.getForum())) {
			return Response.serverError().status(Status.CONFLICT).build();
		}
		return attachToPost(mess, filename, file, request);
	}

